require "rspec"

class PathFollower
  attr_reader :path, :waypoints, :steps
  attr_accessor :position, :heading

  def initialize(path)
    @path = path.split(/\n/)
    @waypoints = []
    @steps = 1
    self.position = find_trailhead
    self.heading = :down
  end

  def walk
    while step; end
  end

  def step
    turn if path_at(*position) == "+"

    return false unless path_at(*next_position)

    advance
    record_waypoint if /[A-Z]/ === path_at(*position)

    true
  end

  def next_position
    row, col = position
    case heading
    when :down
      [row + 1, col]
    when :up
      [row - 1, col]
    when :right
      [row, col + 1]
    when :left
      [row, col - 1]
    end
  end

  def advance
    @steps += 1
    self.position = next_position
  end

  def turn
    possible_headings = case heading
    when :up, :down
      [:right, :left]
    when :right, :left
      [:up, :down]
    end

    self.heading = possible_headings.find do |new_heading|
      move_possible?(new_heading)
    end
  end

  def record_waypoint
    waypoints << path_at(*position)
  end

  private

  def move_possible?(heading)
    row, col = position
    case heading
    when :down
      path_at(row + 1, col)
    when :up
      path_at(row - 1, col)
    when :right
      path_at(row, col + 1)
    when :left
      path_at(row, col - 1)
    end
  end

  def path_at(row, col)
    return nil unless (0...path.length).include?(row)
    return nil unless (0...path[row].length).include?(col)
    path[row][col] == " " ? nil : path[row][col]
  end

  def find_trailhead
    [0, path[0].index("|")]
  end
end

describe PathFollower do
  let(:follower) { PathFollower.new(path) }
  let(:path) do
    <<-PATH
     |          
     |  +--+    
     A  |  C    
 F---|----E|--+ 
     |  |  |  D 
     +B-+  +--+ 
    PATH
  end

  specify "starting position" do
    expect(follower.position).to eq [0, 5]
  end

  specify "the sample puzzle" do
    follower.walk
    expect(follower.waypoints.join).to eq "ABCDEF"
    expect(follower.steps).to eq 38
  end

  describe "step" do
    it "moves vertically down to the next position" do
      expect { follower.step }.to change { follower.position }.to [1, 5]
    end

    it "turns on a +" do
      follower.position = [5, 5]
      expect { follower.step }.to change { follower.position }.to [5, 6]
    end

    it "records waypoints" do
      follower.position = [1, 5]
      expect { follower.step }.to change { follower.waypoints }.to ["A"]
    end
  end
end

return if /rspec$/ === $PROGRAM_NAME

